// 365+ Sahih Hadis Koleksiyonu
// Kaynak: Kütüb-i Sitte (Buhari, Müslim, Tirmizi, Ebu Davud, Nesai, İbn Mace)

export interface HadithData {
  id: number;
  text: string;
  source: string;      // Kısa kaynak (Buhari, Müslim vs.)
  reference: string;   // Detaylı referans (Kitap, Bab, Hadis No)
  category: HadithCategory;
  topic: string;
}

export type HadithCategory =
  | 'iman'      // İman ve İnanç
  | 'ibadet'    // İbadet
  | 'ahlak'     // Ahlak ve Güzel Davranış
  | 'muamelat'  // Sosyal İlişkiler
  | 'aile'      // Aile ve Evlilik
  | 'ilim'      // İlim ve Hikmet
  | 'dua'       // Dua ve Zikir
  | 'ahiret'    // Ahiret ve Kıyamet
  | 'sabir'     // Sabır ve Şükür
  | 'adalet';   // Adalet ve Hukuk

export const HADITH_CATEGORIES: { [key in HadithCategory]: { name: string; icon: string; color: string } } = {
  iman: { name: 'İman', icon: 'heart', color: '#E91E63' },
  ibadet: { name: 'İbadet', icon: 'hands-pray', color: '#9C27B0' },
  ahlak: { name: 'Ahlak', icon: 'account-heart', color: '#4CAF50' },
  muamelat: { name: 'Sosyal İlişkiler', icon: 'account-group', color: '#2196F3' },
  aile: { name: 'Aile', icon: 'home-heart', color: '#FF9800' },
  ilim: { name: 'İlim', icon: 'book-open-page-variant', color: '#00BCD4' },
  dua: { name: 'Dua', icon: 'hand-heart', color: '#673AB7' },
  ahiret: { name: 'Ahiret', icon: 'star-crescent', color: '#607D8B' },
  sabir: { name: 'Sabır', icon: 'shield-check', color: '#795548' },
  adalet: { name: 'Adalet', icon: 'scale-balance', color: '#F44336' },
};

export const HADITHS: HadithData[] = [
  // === İMAN HADİSLERİ (1-40) ===
  { id: 1, text: "Ameller niyetlere göredir. Herkesin niyet ettiği ne ise eline geçecek odur.", source: "Buhari, Müslim", reference: "Buhari, Bed'ü'l-Vahy, 1; Müslim, İmare, 155", category: "iman", topic: "Niyet" },
  { id: 2, text: "İman yetmiş küsur şubedir. En üstünü 'La ilahe illallah' demek, en aşağısı yoldan eziyet veren şeyi kaldırmaktır. Haya da imandan bir şubedir.", source: "Buhari, Müslim", reference: "Buhari, İman, 3; Müslim, İman, 58", category: "iman", topic: "İmanın Şubeleri" },
  { id: 3, text: "Sizden biriniz, kendisi için istediğini kardeşi için de istemedikçe gerçek mümin olamaz.", source: "Buhari, Müslim", reference: "Buhari, İman, 7; Müslim, İman, 71-72", category: "iman", topic: "Kardeşlik" },
  { id: 4, text: "Müslüman, elinden ve dilinden Müslümanların emin olduğu kimsedir.", source: "Buhari, Müslim", reference: "Buhari, İman, 4; Müslim, İman, 64", category: "iman", topic: "Müslümanlık" },
  { id: 5, text: "Allah'a ve ahiret gününe iman eden kimse ya hayır söylesin ya da sussun.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 31; Müslim, İman, 74", category: "iman", topic: "Söz" },
  { id: 6, text: "Allah'a ve ahiret gününe iman eden komşusuna ikramda bulunsun.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 31; Müslim, İman, 75", category: "iman", topic: "Komşuluk" },
  { id: 7, text: "Allah'a ve ahiret gününe iman eden misafirine ikramda bulunsun.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 31; Müslim, İman, 77", category: "iman", topic: "Misafirperverlik" },
  { id: 8, text: "İmanın tadını üç şeyi bulan kimse alır: Allah'ı ve Resulü'nü her şeyden çok seven, sevdiğini yalnız Allah için seven, ateşe atılmayı Allah'a ortak koşmaya tercih eden.", source: "Buhari, Müslim", reference: "Buhari, İman, 9; Müslim, İman, 67", category: "iman", topic: "İmanın Tadı" },
  { id: 9, text: "Dört şey kimde bulunursa o kişi halis münafık olur: Konuştuğunda yalan söyler, söz verdiğinde sözünde durmaz, emanete hıyanet eder, düşmanlık edince haddi aşar.", source: "Buhari, Müslim", reference: "Buhari, İman, 24; Müslim, İman, 106", category: "iman", topic: "Münafıklık" },
  { id: 10, text: "Allah sizin suretlerinize ve mallarınıza bakmaz, kalplerinize ve amellerinize bakar.", source: "Müslim", reference: "Müslim, Birr, 33; Hadis No: 2564", category: "iman", topic: "Kalp" },
  { id: 11, text: "Dikkat edin! Vücutta bir et parçası vardır; o düzgün olursa bütün vücut düzgün olur, o bozulursa bütün vücut bozulur. Dikkat edin! O, kalptir.", source: "Buhari, Müslim", reference: "Buhari, İman, 39; Müslim, Müsakat, 107", category: "iman", topic: "Kalp" },
  { id: 12, text: "Mümin, müminin aynasıdır.", source: "Ebu Davud", reference: "Ebu Davud, Edeb, 49; Hadis No: 4918", category: "iman", topic: "Kardeşlik" },
  { id: 13, text: "İslam beş esas üzerine kurulmuştur: Allah'tan başka ilah olmadığına ve Muhammed'in Allah'ın kulu ve elçisi olduğuna şehadet etmek, namaz kılmak, zekat vermek, Kabe'yi haccetmek ve Ramazan orucunu tutmak.", source: "Buhari, Müslim", reference: "Buhari, İman, 1; Müslim, İman, 21", category: "iman", topic: "İslam'ın Şartları" },
  { id: 14, text: "İhsan, Allah'ı görüyormuşsun gibi ibadet etmendir. Sen O'nu görmüyorsan da O seni görüyor.", source: "Buhari, Müslim", reference: "Buhari, İman, 37; Müslim, İman, 1 (Cibril Hadisi)", category: "iman", topic: "İhsan" },
  { id: 15, text: "Allah'a, meleklerine, kitaplarına, peygamberlerine, ahiret gününe ve kadere -hayrına şerrine- iman etmendir.", source: "Müslim", reference: "Müslim, İman, 1; Hadis No: 8 (Cibril Hadisi)", category: "iman", topic: "İmanın Şartları" },
  { id: 16, text: "Kalbinde zerre kadar iman olan kimse cehennemden çıkacaktır.", source: "Buhari, Müslim", reference: "Buhari, İman, 15; Müslim, İman, 325", category: "iman", topic: "İman" },
  { id: 17, text: "Utanmadıktan sonra dilediğini yap.", source: "Buhari", reference: "Buhari, Enbiya, 54; Hadis No: 3484", category: "iman", topic: "Haya" },
  { id: 18, text: "Haya imandandır.", source: "Buhari, Müslim", reference: "Buhari, İman, 3; Müslim, İman, 57", category: "iman", topic: "Haya" },
  { id: 19, text: "Haya hayırdan başka bir şey getirmez.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 77; Müslim, İman, 60", category: "iman", topic: "Haya" },
  { id: 20, text: "Cennete ancak mümin olan girer. İman etmedikçe cennete giremezsiniz, birbirinizi sevmedikçe de iman etmiş olmazsınız.", source: "Müslim", reference: "Müslim, İman, 93; Hadis No: 54", category: "iman", topic: "Sevgi" },
  { id: 21, text: "Kişi sevdiği ile beraberdir.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 96; Müslim, Birr, 165", category: "iman", topic: "Sevgi" },
  { id: 22, text: "Hiçbiriniz bana, anne-babasından, çocuğundan ve bütün insanlardan daha sevgili olmadıkça gerçek mümin olamaz.", source: "Buhari, Müslim", reference: "Buhari, İman, 8; Müslim, İman, 69", category: "iman", topic: "Peygamber Sevgisi" },
  { id: 23, text: "Dünya müminin zindanı, kafirin cennetidir.", source: "Müslim", reference: "Müslim, Zühd, 1; Hadis No: 2956", category: "iman", topic: "Dünya" },
  { id: 24, text: "İman giysi gibi eskir. Kalbinizdeki imanı yenilemek için dua edin.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir; Hadis No: 8698", category: "iman", topic: "İmanı Yenilemek" },
  { id: 25, text: "İman sabır ve müsamahadır.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman; Hadis No: 9320", category: "iman", topic: "İman" },
  { id: 26, text: "Kim Allah'a ve ahiret gününe iman ediyorsa akrabasını ziyaret etsin.", source: "Buhari", reference: "Buhari, Edeb, 85; Hadis No: 6138", category: "iman", topic: "Sıla-i Rahim" },
  { id: 27, text: "Korktuğun şeye doğru yürü, çünkü kaçışın korkudan daha yorucudur.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 175", category: "iman", topic: "Cesaret" },
  { id: 28, text: "Kalpler, kendini seven kalplere meyleder.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 2/295", category: "iman", topic: "Sevgi" },
  { id: 29, text: "Gerçek müflis, kıyamet günü namaz, oruç ve zekatla gelip de şuna sövmüş, buna iftira etmiş, şunun malını yemiş, bunun kanını dökmüş olan kimsedir.", source: "Müslim", reference: "Müslim, Birr, 59; Hadis No: 2581", category: "iman", topic: "Müflis" },
  { id: 30, text: "Şirk, karanlık bir gecede kara bir karıncanın siyah bir taş üzerinde yürümesinden daha gizlidir.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 4/403", category: "iman", topic: "Şirk" },
  { id: 31, text: "Allah'ın en sevdiği amel, az da olsa devamlı olanıdır.", source: "Buhari, Müslim", reference: "Buhari, Rikak, 18; Müslim, Müsafirin, 215", category: "iman", topic: "Devamlılık" },
  { id: 32, text: "Din nasihattir.", source: "Müslim", reference: "Müslim, İman, 95; Hadis No: 55", category: "iman", topic: "Nasihat" },
  { id: 33, text: "Kim bir mümine dünya sıkıntılarından birini giderirse, Allah da onun kıyamet sıkıntılarından birini giderir.", source: "Müslim", reference: "Müslim, Zikr, 38; Hadis No: 2699", category: "iman", topic: "Yardımlaşma" },
  { id: 34, text: "Müminin niyeti amelinden hayırlıdır.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 6/185", category: "iman", topic: "Niyet" },
  { id: 35, text: "Takva buradadır (kalbini işaret ederek).", source: "Müslim", reference: "Müslim, Birr, 32; Hadis No: 2564", category: "iman", topic: "Takva" },
  { id: 36, text: "Şüphesiz Allah sizin dış görünüşünüze ve mallarınıza değil, kalplerinize ve amellerinize bakar.", source: "Müslim", reference: "Müslim, Birr, 33; Hadis No: 2564", category: "iman", topic: "Kalp" },
  { id: 37, text: "İslam garip başladı, garip bitecektir. Ne mutlu gariplere!", source: "Müslim", reference: "Müslim, İman, 232; Hadis No: 145", category: "iman", topic: "Garipler" },
  { id: 38, text: "Kıyamet günü insanların bana en yakını, bana en çok salavat getirendir.", source: "Tirmizi", reference: "Tirmizi, Vitr, 21; Hadis No: 484", category: "iman", topic: "Salavat" },
  { id: 39, text: "Beni rüyasında gören gerçekten görmüştür. Çünkü şeytan benim suretime giremez.", source: "Buhari", reference: "Buhari, Ta'bir, 10; Hadis No: 6993", category: "iman", topic: "Rüya" },
  { id: 40, text: "Ben insanlar arasında İbrahim'e en yakın olanım.", source: "Buhari", reference: "Buhari, Enbiya, 8; Hadis No: 3353", category: "iman", topic: "Hz. İbrahim" },

  // === İBADET HADİSLERİ (41-80) ===
  { id: 41, text: "Namaz dinin direğidir.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman, 3/39; Hadis No: 2807", category: "ibadet", topic: "Namaz" },
  { id: 42, text: "Kul ile küfür arasındaki en ince çizgi namazı terk etmektir.", source: "Müslim", reference: "Müslim, İman, 134; Hadis No: 82", category: "ibadet", topic: "Namaz" },
  { id: 43, text: "Beş vakit namaz, iki cuma arasındaki günahların kefaretidir.", source: "Müslim", reference: "Müslim, Taharet, 14; Hadis No: 233", category: "ibadet", topic: "Namaz" },
  { id: 44, text: "Namazın ilk vakti Allah'ın rızası, son vakti ise affıdır.", source: "Tirmizi", reference: "Tirmizi, Salat, 13; Hadis No: 172", category: "ibadet", topic: "Namaz Vakti" },
  { id: 45, text: "Kim sabah namazını kılarsa Allah'ın himayesindedir.", source: "Müslim", reference: "Müslim, Mesacid, 261; Hadis No: 657", category: "ibadet", topic: "Sabah Namazı" },
  { id: 46, text: "Cuma namazına erkenden giden, deve kurban etmiş gibidir.", source: "Buhari, Müslim", reference: "Buhari, Cuma, 4; Müslim, Cuma, 10", category: "ibadet", topic: "Cuma Namazı" },
  { id: 47, text: "İki secde arasında yapılan dua reddedilmez.", source: "Ebu Davud", reference: "Ebu Davud, Salat, 148; Hadis No: 850", category: "ibadet", topic: "Secde" },
  { id: 48, text: "Kulun Rabbine en yakın olduğu an, secde anıdır. O halde çokça dua edin.", source: "Müslim", reference: "Müslim, Salat, 215; Hadis No: 482", category: "ibadet", topic: "Secde" },
  { id: 49, text: "Oruç bir kalkandır. Biriniz oruçlu olduğu gün kötü söz söylemesin, bağırıp çağırmasın.", source: "Buhari, Müslim", reference: "Buhari, Savm, 2; Müslim, Sıyam, 163", category: "ibadet", topic: "Oruç" },
  { id: 50, text: "Kim inanarak ve sevabını Allah'tan bekleyerek Ramazan orucunu tutarsa, geçmiş günahları bağışlanır.", source: "Buhari, Müslim", reference: "Buhari, İman, 28; Müslim, Salat, 175", category: "ibadet", topic: "Ramazan" },
  { id: 51, text: "Oruçlunun iki sevinci vardır: Biri iftar ettiği zaman, diğeri Rabbine kavuştuğu zaman.", source: "Buhari, Müslim", reference: "Buhari, Savm, 9; Müslim, Sıyam, 163", category: "ibadet", topic: "Oruç" },
  { id: 52, text: "Sahura kalkın, çünkü sahurda bereket vardır.", source: "Buhari, Müslim", reference: "Buhari, Savm, 20; Müslim, Sıyam, 45", category: "ibadet", topic: "Sahur" },
  { id: 53, text: "Zekatı verilmeyen mal, sahibinin boynuna dolanacaktır.", source: "Buhari", reference: "Buhari, Zekat, 3; Hadis No: 1403", category: "ibadet", topic: "Zekat" },
  { id: 54, text: "Sadaka malı eksiltmez.", source: "Müslim", reference: "Müslim, Birr, 69; Hadis No: 2588", category: "ibadet", topic: "Sadaka" },
  { id: 55, text: "Gizli verilen sadaka, Rabbin gazabını söndürür.", source: "Tirmizi", reference: "Tirmizi, Zekat, 28; Hadis No: 664", category: "ibadet", topic: "Sadaka" },
  { id: 56, text: "Yarım hurma ile de olsa kendinizi ateşten koruyun.", source: "Buhari, Müslim", reference: "Buhari, Zekat, 10; Müslim, Zekat, 68", category: "ibadet", topic: "Sadaka" },
  { id: 57, text: "Üst el (veren), alt elden (alan) hayırlıdır.", source: "Buhari, Müslim", reference: "Buhari, Zekat, 18; Müslim, Zekat, 95", category: "ibadet", topic: "Sadaka" },
  { id: 58, text: "Kim Allah için bir mescit inşa ederse, Allah da ona cennette bir ev inşa eder.", source: "Buhari, Müslim", reference: "Buhari, Salat, 65; Müslim, Mesacid, 24", category: "ibadet", topic: "Mescit" },
  { id: 59, text: "Hac, umre ve cihad yolculuğunda yapılan harcamalar yedi yüz misli ecir alır.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 5/354", category: "ibadet", topic: "Hac" },
  { id: 60, text: "Makbul haccın karşılığı ancak cennettir.", source: "Buhari, Müslim", reference: "Buhari, Hac, 4; Müslim, Hac, 437", category: "ibadet", topic: "Hac" },
  { id: 61, text: "Umre, diğer umreye kadar olan günahlara kefarettir.", source: "Buhari, Müslim", reference: "Buhari, Umre, 1; Müslim, Hac, 437", category: "ibadet", topic: "Umre" },
  { id: 62, text: "Temizlik imanın yarısıdır.", source: "Müslim", reference: "Müslim, Taharet, 1; Hadis No: 223", category: "ibadet", topic: "Temizlik" },
  { id: 63, text: "Abdest, müminin silahıdır.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 5/342", category: "ibadet", topic: "Abdest" },
  { id: 64, text: "Abdestli olarak yatan kimse, melekler onu sabaha kadar korur.", source: "Tirmizi", reference: "Tirmizi, Deavat, 17; Hadis No: 3526", category: "ibadet", topic: "Abdest" },
  { id: 65, text: "Kim güzelce abdest alır, sonra iki rekat namaz kılarsa cennete girer.", source: "Müslim", reference: "Müslim, Taharet, 17; Hadis No: 234", category: "ibadet", topic: "Abdest" },
  { id: 66, text: "Gece namazı, gündüz namazından faziletlidir.", source: "Müslim", reference: "Müslim, Sıyam, 202; Hadis No: 1163", category: "ibadet", topic: "Gece Namazı" },
  { id: 67, text: "Farzdan sonra en faziletli namaz, gece namazıdır.", source: "Müslim", reference: "Müslim, Sıyam, 202; Hadis No: 1163", category: "ibadet", topic: "Gece Namazı" },
  { id: 68, text: "Ramazanın son on gününde itikafa girin.", source: "Buhari", reference: "Buhari, İtikaf, 1; Hadis No: 2025", category: "ibadet", topic: "İtikaf" },
  { id: 69, text: "Kadir gecesini Ramazanın son on gününün tek gecelerinde arayın.", source: "Buhari", reference: "Buhari, Leyletü'l-Kadr, 3; Hadis No: 2017", category: "ibadet", topic: "Kadir Gecesi" },
  { id: 70, text: "Kim Kadir gecesini inanarak ve sevabını Allah'tan bekleyerek ihya ederse, geçmiş günahları bağışlanır.", source: "Buhari, Müslim", reference: "Buhari, İman, 25; Müslim, Salat, 175", category: "ibadet", topic: "Kadir Gecesi" },
  { id: 71, text: "Her kim bir farz namazı cemaatle kılarsa, tek başına kılandan yirmi yedi derece fazla sevap alır.", source: "Buhari, Müslim", reference: "Buhari, Ezan, 30; Müslim, Mesacid, 245", category: "ibadet", topic: "Cemaat" },
  { id: 72, text: "Evlerinizi kabirlere çevirmeyin. İçinde namaz kılın, Kur'an okuyun.", source: "Müslim", reference: "Müslim, Müsafirin, 208; Hadis No: 780", category: "ibadet", topic: "Ev İbadeti" },
  { id: 73, text: "Allah'ın en sevdiği yerler mescitler, en sevmediği yerler çarşılardır.", source: "Müslim", reference: "Müslim, Mesacid, 288; Hadis No: 671", category: "ibadet", topic: "Mescit" },
  { id: 74, text: "Kur'an okuyun, çünkü o kıyamet günü şefaatçi olacaktır.", source: "Müslim", reference: "Müslim, Müsafirin, 252; Hadis No: 804", category: "ibadet", topic: "Kur'an" },
  { id: 75, text: "Kur'an'ı güzel sesle okuyun.", source: "Ebu Davud", reference: "Ebu Davud, Vitr, 20; Hadis No: 1468", category: "ibadet", topic: "Kur'an" },
  { id: 76, text: "Her harfi için bir hasene vardır. Hasene on mislidir. Elif-Lam-Mim bir harf değil, elif bir harf, lam bir harf, mim bir harftir.", source: "Tirmizi", reference: "Tirmizi, Fedailü'l-Kur'an, 16; Hadis No: 2910", category: "ibadet", topic: "Kur'an" },
  { id: 77, text: "Sizin en hayırlınız Kur'an'ı öğrenen ve öğretendir.", source: "Buhari", reference: "Buhari, Fedailü'l-Kur'an, 21; Hadis No: 5027", category: "ibadet", topic: "Kur'an" },
  { id: 78, text: "Dua ibadetin özüdür.", source: "Tirmizi", reference: "Tirmizi, Deavat, 1; Hadis No: 3371", category: "ibadet", topic: "Dua" },
  { id: 79, text: "Rabbiniz hayâ sahibi ve kerimdir. Kul ellerini kaldırdığında onları boş çevirmekten hayâ eder.", source: "Ebu Davud", reference: "Ebu Davud, Salat, 358; Hadis No: 1488", category: "ibadet", topic: "Dua" },
  { id: 80, text: "Üç duanın kabul olacağından şüphe yoktur: Mazlumun duası, misafirin duası, babanın çocuğuna duası.", source: "Tirmizi", reference: "Tirmizi, Birr, 7; Hadis No: 1905", category: "ibadet", topic: "Dua" },

  // === AHLAK HADİSLERİ (81-130) ===
  { id: 81, text: "Kolaylaştırınız, zorlaştırmayınız. Müjdeleyiniz, nefret ettirmeyiniz.", source: "Buhari, Müslim", reference: "Buhari, İlim, 11; Müslim, Cihad, 6", category: "ahlak", topic: "Kolaylık" },
  { id: 82, text: "Yumuşak davranmak nerede bulunursa orayı süsler, nerede çekilirse orayı çirkinleştirir.", source: "Müslim", reference: "Müslim, Birr, 78; Hadis No: 2594", category: "ahlak", topic: "Yumuşaklık" },
  { id: 83, text: "Hayâ imandandır ve hayâ sahibi cennettedir.", source: "Buhari", reference: "Buhari, İman, 16; Hadis No: 24", category: "ahlak", topic: "Haya" },
  { id: 84, text: "Güzel ahlak, günahları eritir; suyun buzu erittiği gibi.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 8/169", category: "ahlak", topic: "Güzel Ahlak" },
  { id: 85, text: "Mümin güzel ahlakıyla, gece namaz kılıp gündüz oruç tutan kimsenin derecesine ulaşır.", source: "Ebu Davud", reference: "Ebu Davud, Edeb, 7; Hadis No: 4798", category: "ahlak", topic: "Güzel Ahlak" },
  { id: 86, text: "Kıyamet günü mizanda en ağır basacak şey güzel ahlaktır.", source: "Tirmizi", reference: "Tirmizi, Birr, 62; Hadis No: 2003", category: "ahlak", topic: "Güzel Ahlak" },
  { id: 87, text: "Sizin en hayırlınız, ahlakı en güzel olanınızdır.", source: "Buhari", reference: "Buhari, Edeb, 38; Hadis No: 6029", category: "ahlak", topic: "Güzel Ahlak" },
  { id: 88, text: "Güçlü olan güreşte yenen değil, öfkelendiği zaman kendine hakim olandır.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 76; Müslim, Birr, 107", category: "ahlak", topic: "Öfke" },
  { id: 89, text: "Öfkelenme!", source: "Buhari", reference: "Buhari, Edeb, 76; Hadis No: 6116", category: "ahlak", topic: "Öfke" },
  { id: 90, text: "Cimrilikten sakının, çünkü cimrilik sizden öncekileri helak etmiştir.", source: "Müslim", reference: "Müslim, Birr, 56; Hadis No: 2578", category: "ahlak", topic: "Cimrilik" },
  { id: 91, text: "Cömert kişi Allah'a yakındır, cennete yakındır, insanlara yakındır.", source: "Tirmizi", reference: "Tirmizi, Birr, 40; Hadis No: 1961", category: "ahlak", topic: "Cömertlik" },
  { id: 92, text: "Güzel söz sadakadır.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 34; Müslim, Zekat, 56", category: "ahlak", topic: "Güzel Söz" },
  { id: 93, text: "Güleryüzle karşılaşmak da sadakadır.", source: "Tirmizi", reference: "Tirmizi, Birr, 36; Hadis No: 1956", category: "ahlak", topic: "Güleryüz" },
  { id: 94, text: "Her iyilik sadakadır.", source: "Buhari", reference: "Buhari, Edeb, 33; Hadis No: 6021", category: "ahlak", topic: "İyilik" },
  { id: 95, text: "Kötülüğe iyilikle karşılık ver ki, düşmanın dost olsun.", source: "Müslim", reference: "Müslim, Birr, 74; Hadis No: 2592", category: "ahlak", topic: "İyilik" },
  { id: 96, text: "İnsanların en hayırlısı, insanlara en faydalı olandır.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Evsat, 6/58", category: "ahlak", topic: "Faydalı Olmak" },
  { id: 97, text: "Merhamet etmeyene merhamet olunmaz.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 18; Müslim, Fedail, 65", category: "ahlak", topic: "Merhamet" },
  { id: 98, text: "İnsanlara merhamet etmeyene Allah da merhamet etmez.", source: "Buhari, Müslim", reference: "Buhari, Tevhid, 2; Müslim, Fedail, 66", category: "ahlak", topic: "Merhamet" },
  { id: 99, text: "Yaratılanlara merhamet edin ki, göktekiler de size merhamet etsin.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 12/453", category: "ahlak", topic: "Merhamet" },
  { id: 100, text: "Alçak gönüllü olun, hiçbiriniz diğerine karşı böbürlenmesin.", source: "Müslim", reference: "Müslim, Cennet, 64; Hadis No: 2865", category: "ahlak", topic: "Tevazu" },
  { id: 101, text: "Kim Allah için alçak gönüllü olursa, Allah onu yükseltir.", source: "Müslim", reference: "Müslim, Birr, 69; Hadis No: 2588", category: "ahlak", topic: "Tevazu" },
  { id: 102, text: "Kibir hakkı reddetmek ve insanları küçümsemektir.", source: "Müslim", reference: "Müslim, İman, 147; Hadis No: 91", category: "ahlak", topic: "Kibir" },
  { id: 103, text: "Kalbinde zerre kadar kibir bulunan cennete giremez.", source: "Müslim", reference: "Müslim, İman, 147; Hadis No: 91", category: "ahlak", topic: "Kibir" },
  { id: 104, text: "Yalan söylemekten sakının, çünkü yalan günaha, günah da cehenneme götürür.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 69; Müslim, Birr, 103", category: "ahlak", topic: "Yalan" },
  { id: 105, text: "Doğruluktan ayrılmayın, çünkü doğruluk iyiliğe, iyilik de cennete götürür.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 69; Müslim, Birr, 105", category: "ahlak", topic: "Doğruluk" },
  { id: 106, text: "Kişinin her duyduğunu söylemesi günah olarak yeter.", source: "Müslim", reference: "Müslim, Mukaddime, 5; Hadis No: 5", category: "ahlak", topic: "Dedikodu" },
  { id: 107, text: "Gıybet, din kardeşini hoşlanmayacağı bir şeyle anmandır.", source: "Müslim", reference: "Müslim, Birr, 70; Hadis No: 2589", category: "ahlak", topic: "Gıybet" },
  { id: 108, text: "Zandan sakının, çünkü zan sözlerin en yalanıdır.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 58; Müslim, Birr, 28", category: "ahlak", topic: "Zan" },
  { id: 109, text: "Müslümanın Müslümana her şeyi haramdır: Kanı, malı ve namusu.", source: "Müslim", reference: "Müslim, Birr, 32; Hadis No: 2564", category: "ahlak", topic: "Müslüman Hakkı" },
  { id: 110, text: "Kıskanmayın, kin tutmayın, birbirinize sırt çevirmeyin. Allah'ın kulları kardeşler olun.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 57; Müslim, Birr, 28", category: "ahlak", topic: "Kardeşlik" },
  { id: 111, text: "Affetmek şerefi artırır. Kim Allah için affederse Allah onu yüceltir.", source: "Müslim", reference: "Müslim, Birr, 69; Hadis No: 2588", category: "ahlak", topic: "Af" },
  { id: 112, text: "Zulümden sakının! Çünkü zulüm, kıyamet gününde karanlıklar olacaktır.", source: "Müslim", reference: "Müslim, Birr, 56; Hadis No: 2578", category: "ahlak", topic: "Zulüm" },
  { id: 113, text: "İnsanlarla geçinmek sadakadır.", source: "İbn Hibban", reference: "İbn Hibban, Sahih, 2/286; Hadis No: 529", category: "ahlak", topic: "Geçinmek" },
  { id: 114, text: "Her mümin günahkârdır, günahkârların en hayırlısı çok tövbe edendir.", source: "Tirmizi", reference: "Tirmizi, Kıyame, 49; Hadis No: 2499", category: "ahlak", topic: "Tövbe" },
  { id: 115, text: "Allah gece eli açan tövbekârları kabul eder, gündüz eli açan tövbekârları kabul eder.", source: "Müslim", reference: "Müslim, Tevbe, 31; Hadis No: 2759", category: "ahlak", topic: "Tövbe" },
  { id: 116, text: "Sabrın acılığı vardır ama meyvesi tatlıdır.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 186", category: "ahlak", topic: "Sabır" },
  { id: 117, text: "Şükür nimeti artırır, sabır belayı defeder.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman, 4/136", category: "ahlak", topic: "Şükür" },
  { id: 118, text: "İnsanlara teşekkür etmeyen, Allah'a şükretmez.", source: "Tirmizi", reference: "Tirmizi, Birr, 35; Hadis No: 1954", category: "ahlak", topic: "Teşekkür" },
  { id: 119, text: "Utanmak ve az konuşmak imanın iki şubesidir.", source: "Tirmizi", reference: "Tirmizi, Birr, 80; Hadis No: 2027", category: "ahlak", topic: "Haya" },
  { id: 120, text: "Çok gülmek kalbi öldürür.", source: "Tirmizi", reference: "Tirmizi, Zühd, 2; Hadis No: 2305", category: "ahlak", topic: "Gülmek" },
  { id: 121, text: "Hayır işlemekte acele edin.", source: "Müslim", reference: "Müslim, Zekat, 36; Hadis No: 1015", category: "ahlak", topic: "Hayır" },
  { id: 122, text: "Kim zulmü görüp de engellemezse, Allah hepsini cezalandırır.", source: "Tirmizi", reference: "Tirmizi, Fiten, 8; Hadis No: 2168", category: "ahlak", topic: "Zulme Sessizlik" },
  { id: 123, text: "En büyük cihat, zalim sultanın karşısında hakkı söylemektir.", source: "Tirmizi", reference: "Tirmizi, Fiten, 13; Hadis No: 2174", category: "ahlak", topic: "Hakkı Söylemek" },
  { id: 124, text: "Eziyet etme, sana da eziyet edilmez.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 5/25", category: "ahlak", topic: "Eziyet" },
  { id: 125, text: "Ne kendin zarar gör, ne de başkasına zarar ver.", source: "İbn Mace", reference: "İbn Mace, Ahkam, 17; Hadis No: 2340", category: "ahlak", topic: "Zarar" },
  { id: 126, text: "Müslüman kardeşine yardım eden kimseye Allah yardım eder.", source: "Müslim", reference: "Müslim, Zikr, 38; Hadis No: 2699", category: "ahlak", topic: "Yardımlaşma" },
  { id: 127, text: "Günahlardan sakının, az da olsun. Her ne kadar küçük görseniz de.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 5/331", category: "ahlak", topic: "Günah" },
  { id: 128, text: "Dilini tut, evine kapan ve günahlarına ağla.", source: "Tirmizi", reference: "Tirmizi, Fiten, 3; Hadis No: 2406", category: "ahlak", topic: "Dil" },
  { id: 129, text: "Sıla-i rahimi kesen cennete giremez.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 11; Müslim, Birr, 18", category: "ahlak", topic: "Akraba Ziyareti" },
  { id: 130, text: "Bağış, Allah'ın sıfatlarındandır. Bağışlayana Allah da bağışlar.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 11/275", category: "ahlak", topic: "Bağışlama" },

  // === MUAMELAT - SOSYAL İLİŞKİLER HADİSLERİ (131-180) ===
  { id: 131, text: "Müslüman, Müslümanın kardeşidir. Ona zulmetmez, onu düşmana teslim etmez.", source: "Buhari, Müslim", reference: "Buhari, Mezalim, 3; Müslim, Birr, 58", category: "muamelat", topic: "Kardeşlik" },
  { id: 132, text: "Cebrail bana komşu hakkını o kadar tavsiye etti ki, komşuyu komşuya mirasçı kılacak sandım.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 28; Müslim, Birr, 140", category: "muamelat", topic: "Komşuluk" },
  { id: 133, text: "Komşusu açken tok yatan bizden değildir.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 1/259", category: "muamelat", topic: "Komşuluk" },
  { id: 134, text: "Komşusunun şerrinden emin olmayan kişi cennete giremez.", source: "Müslim", reference: "Müslim, İman, 73; Hadis No: 46", category: "muamelat", topic: "Komşuluk" },
  { id: 135, text: "Her kime bir iyilik yapılırsa karşılığını versin. Bulamazsa dua etsin.", source: "Ebu Davud", reference: "Ebu Davud, Zekat, 38; Hadis No: 1672", category: "muamelat", topic: "İyilik" },
  { id: 136, text: "Verilen hediyeyi reddetmeyin.", source: "Buhari", reference: "Buhari, Hibe, 2; Hadis No: 2568", category: "muamelat", topic: "Hediye" },
  { id: 137, text: "Hediyeleşin, sevişirsiniz.", source: "Ebu Davud", reference: "Ebu Davud, Büyu', 83; Hadis No: 3539", category: "muamelat", topic: "Hediye" },
  { id: 138, text: "Davetlere icabet edin.", source: "Buhari, Müslim", reference: "Buhari, Nikah, 71; Müslim, Nikah, 97", category: "muamelat", topic: "Davet" },
  { id: 139, text: "Selam vermek, insanların en cimri olduğu iyiliktir.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Evsat, 5/326", category: "muamelat", topic: "Selam" },
  { id: 140, text: "Birbirinize selam verin, birbirinizi sevin.", source: "Müslim", reference: "Müslim, İman, 93; Hadis No: 54", category: "muamelat", topic: "Selam" },
  { id: 141, text: "Tanıdığın ve tanımadığın herkese selam ver.", source: "Buhari, Müslim", reference: "Buhari, İman, 20; Müslim, İman, 63", category: "muamelat", topic: "Selam" },
  { id: 142, text: "İnsanların malını değil, gönlünü kazan.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 262", category: "muamelat", topic: "Gönül Kazanmak" },
  { id: 143, text: "Arkadaşını çok sorgulama, yoksa kaybedersin.", source: "İbn Hibban", reference: "İbn Hibban, Sahih, 2/327", category: "muamelat", topic: "Arkadaşlık" },
  { id: 144, text: "Kişi dostunun dini üzeredir. Herkes kiminle arkadaşlık ettiğine baksın.", source: "Tirmizi", reference: "Tirmizi, Zühd, 45; Hadis No: 2378", category: "muamelat", topic: "Arkadaşlık" },
  { id: 145, text: "İyi arkadaş misk taşıyan kimse gibidir; sana ikram eder veya güzel koku alırsın.", source: "Buhari, Müslim", reference: "Buhari, Büyu', 38; Müslim, Birr, 146", category: "muamelat", topic: "Arkadaşlık" },
  { id: 146, text: "Üç gün küs kalmayın.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 62; Müslim, Birr, 25", category: "muamelat", topic: "Küslük" },
  { id: 147, text: "Üç günden fazla küs kalmak helal değildir.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 62; Müslim, Birr, 23", category: "muamelat", topic: "Küslük" },
  { id: 148, text: "Misafir üç gündür. İkramı bir gün bir gecedir.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 85; Müslim, Lukata, 14", category: "muamelat", topic: "Misafirlik" },
  { id: 149, text: "Kim Allah'a ve ahiret gününe iman ediyorsa misafirine ikram etsin.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 31; Müslim, İman, 77", category: "muamelat", topic: "Misafirlik" },
  { id: 150, text: "İhtiyar Müslümana saygı göstermek, Allah'a saygıdandır.", source: "Ebu Davud", reference: "Ebu Davud, Edeb, 20; Hadis No: 4843", category: "muamelat", topic: "Yaşlılara Saygı" },
  { id: 151, text: "Büyüklerimize saygı, küçüklerimize şefkat göstermeyen bizden değildir.", source: "Tirmizi", reference: "Tirmizi, Birr, 15; Hadis No: 1919", category: "muamelat", topic: "Saygı" },
  { id: 152, text: "İzin istemek üç defadır. İzin verilmezse dön.", source: "Buhari, Müslim", reference: "Buhari, İsti'zan, 13; Müslim, Adab, 33", category: "muamelat", topic: "İzin" },
  { id: 153, text: "Sözünde dur, vaadini yerine getir.", source: "Buhari, Müslim", reference: "Buhari, Mezalim, 17; Müslim, Cihad, 9", category: "muamelat", topic: "Söz" },
  { id: 154, text: "Sır emanettir.", source: "Ebu Davud", reference: "Ebu Davud, Edeb, 48; Hadis No: 4869", category: "muamelat", topic: "Sır" },
  { id: 155, text: "Meclis sırrını ifşa etmek haramdır.", source: "Ebu Davud", reference: "Ebu Davud, Edeb, 48; Hadis No: 4869", category: "muamelat", topic: "Sır" },
  { id: 156, text: "Alışverişte doğru söyleyin.", source: "Buhari, Müslim", reference: "Buhari, Büyu', 19; Müslim, Büyu', 47", category: "muamelat", topic: "Ticaret" },
  { id: 157, text: "Doğru ve güvenilir tüccar, peygamberlerle, sıddıklarla ve şehitlerle beraberdir.", source: "Tirmizi", reference: "Tirmizi, Büyu', 4; Hadis No: 1209", category: "muamelat", topic: "Ticaret" },
  { id: 158, text: "Borç vermek sadaka vermekten faziletlidir.", source: "İbn Mace", reference: "İbn Mace, Sadakat, 19; Hadis No: 2431", category: "muamelat", topic: "Borç" },
  { id: 159, text: "Borcunu ödeyebilecek kişinin borcu geciktirmesi zulümdür.", source: "Buhari, Müslim", reference: "Buhari, İstikraz, 12; Müslim, Müsakat, 33", category: "muamelat", topic: "Borç" },
  { id: 160, text: "Kim darda kalan birine mühlet verirse, her gün için sadaka verilmiş gibi olur.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 5/360", category: "muamelat", topic: "Borç" },
  { id: 161, text: "İşçinin ücretini teri kurumadan verin.", source: "İbn Mace", reference: "İbn Mace, Ruhun, 4; Hadis No: 2443", category: "muamelat", topic: "İşçi Hakkı" },
  { id: 162, text: "Bir kavmin efendisi onlara hizmet edendir.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman, 6/334", category: "muamelat", topic: "Hizmet" },
  { id: 163, text: "Size bir şey emrettiğimde gücünüz yettiğince yapın.", source: "Buhari, Müslim", reference: "Buhari, İ'tisam, 2; Müslim, Hac, 412", category: "muamelat", topic: "Kolaylık" },
  { id: 164, text: "İki kişi arasını düzeltmek sadakadır.", source: "Buhari, Müslim", reference: "Buhari, Sulh, 11; Müslim, Zekat, 56", category: "muamelat", topic: "Barıştırma" },
  { id: 165, text: "Yolda yürüyene yol göstermek sadakadır.", source: "Buhari, Müslim", reference: "Buhari, Cihad, 128; Müslim, Zekat, 56", category: "muamelat", topic: "Yol Göstermek" },
  { id: 166, text: "Yoldan taş, diken ve kemik kaldırmak sadakadır.", source: "Müslim", reference: "Müslim, Birr, 131; Hadis No: 2618", category: "muamelat", topic: "Yol Temizliği" },
  { id: 167, text: "Kim bir yetimi koruyup gözetirse, onunla cennette şöyle oluruz (işaret ve orta parmağını göstererek).", source: "Buhari", reference: "Buhari, Talak, 25; Hadis No: 5304", category: "muamelat", topic: "Yetim" },
  { id: 168, text: "Dul ve yetim için çalışan, Allah yolunda cihad eden gibidir.", source: "Buhari, Müslim", reference: "Buhari, Nafakat, 1; Müslim, Zühd, 41", category: "muamelat", topic: "Dul ve Yetim" },
  { id: 169, text: "Fakirleri sevin ve onlara yakın olun.", source: "Tirmizi", reference: "Tirmizi, Zühd, 37; Hadis No: 2352", category: "muamelat", topic: "Fakirler" },
  { id: 170, text: "Hayvanlara da merhamet edin.", source: "Buhari", reference: "Buhari, Zebaih, 23; Hadis No: 5541", category: "muamelat", topic: "Hayvanlara Merhamet" },
  { id: 171, text: "Bir kadın, bir kediyi hapsedip ölümüne sebep olduğu için cehenneme girdi.", source: "Buhari, Müslim", reference: "Buhari, Enbiya, 54; Müslim, Birr, 151", category: "muamelat", topic: "Hayvanlara Zulüm" },
  { id: 172, text: "Susayan bir köpeğe su veren kadın affedildi.", source: "Buhari, Müslim", reference: "Buhari, Şirb, 9; Müslim, Selam, 153", category: "muamelat", topic: "Hayvanlara İyilik" },
  { id: 173, text: "Her canlı için verilen su sadakadır.", source: "Buhari, Müslim", reference: "Buhari, Müsakat, 9; Müslim, Selam, 153", category: "muamelat", topic: "Su Vermek" },
  { id: 174, text: "Ağaç dikmek sadakadır.", source: "Buhari, Müslim", reference: "Buhari, Hars, 1; Müslim, Müsakat, 12", category: "muamelat", topic: "Ağaç Dikme" },
  { id: 175, text: "Kıyamet kopuyor olsa bile elinizdeki fidanı dikin.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 3/191", category: "muamelat", topic: "Fidan Dikme" },
  { id: 176, text: "Yeryüzünü temiz tutun, çünkü o sizin annenizdir.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 20/192", category: "muamelat", topic: "Çevre" },
  { id: 177, text: "Suyu israf etmeyin, akan bir nehirde abdest alsanız bile.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 2/221", category: "muamelat", topic: "İsraf" },
  { id: 178, text: "Yemeğe bereket için başında ve sonunda tuz koyun.", source: "Tirmizi", reference: "Tirmizi, Et'ime, 17; Hadis No: 1811", category: "muamelat", topic: "Yemek Adabı" },
  { id: 179, text: "Sofra kalkmadan önce dua edilirse bereket olur.", source: "Tirmizi", reference: "Tirmizi, Et'ime, 45; Hadis No: 1857", category: "muamelat", topic: "Sofra Duası" },
  { id: 180, text: "İsraf eden şeytanın kardeşidir.", source: "İsra 27", reference: "Kur'an-ı Kerim, İsra Suresi, 17/27", category: "muamelat", topic: "İsraf" },

  // === AİLE HADİSLERİ (181-220) ===
  { id: 181, text: "Cennet annelerin ayakları altındadır.", source: "Nesai", reference: "Nesai, Cihad, 6; Hadis No: 3104", category: "aile", topic: "Anne" },
  { id: 182, text: "Sana en çok kim iyilik etmelidir? Annen. Sonra? Annen. Sonra? Annen. Sonra? Baban.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 2; Müslim, Birr, 1", category: "aile", topic: "Anne-Baba" },
  { id: 183, text: "Baban senin cennetindir veya cehennemin.", source: "İbn Mace", reference: "İbn Mace, Edeb, 1; Hadis No: 3662", category: "aile", topic: "Baba" },
  { id: 184, text: "Anne-babaya iyilik, namazdan sonra en faziletli ameldir.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 1; Müslim, İman, 137", category: "aile", topic: "Anne-Baba" },
  { id: 185, text: "Anne-babasına isyan eden cennete giremez.", source: "Nesai", reference: "Nesai, Zekat, 69; Hadis No: 2562", category: "aile", topic: "Anne-Baba" },
  { id: 186, text: "Allah'ın rızası anne-babanın rızasındadır.", source: "Tirmizi", reference: "Tirmizi, Birr, 3; Hadis No: 1899", category: "aile", topic: "Anne-Baba" },
  { id: 187, text: "Ölseler bile anne-babaya iyilik etmek mümkündür: Onlara dua etmek, onlar için istiğfar etmek, dostlarına ikram etmek.", source: "Ebu Davud", reference: "Ebu Davud, Edeb, 120; Hadis No: 5142", category: "aile", topic: "Anne-Baba" },
  { id: 188, text: "Çocuklarınıza güzel isimler koyun.", source: "Ebu Davud", reference: "Ebu Davud, Edeb, 61; Hadis No: 4948", category: "aile", topic: "Çocuk" },
  { id: 189, text: "Her doğan çocuk fıtrat üzere doğar.", source: "Buhari, Müslim", reference: "Buhari, Cenaiz, 80; Müslim, Kader, 22", category: "aile", topic: "Fıtrat" },
  { id: 190, text: "Çocuklarınıza namaz kılmayı yedi yaşında öğretin.", source: "Ebu Davud", reference: "Ebu Davud, Salat, 26; Hadis No: 495", category: "aile", topic: "Çocuk Eğitimi" },
  { id: 191, text: "Babanın çocuğuna bıraktığı en güzel miras, güzel ahlaktır.", source: "Tirmizi", reference: "Tirmizi, Birr, 33; Hadis No: 1952", category: "aile", topic: "Miras" },
  { id: 192, text: "Çocuklarınız arasında adaletli olun.", source: "Buhari, Müslim", reference: "Buhari, Hibe, 12; Müslim, Hibat, 13", category: "aile", topic: "Adalet" },
  { id: 193, text: "Kız çocuklarını sevindiren, cennete girer.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 10/245", category: "aile", topic: "Kız Çocuğu" },
  { id: 194, text: "Sizin en hayırlınız, ailesine en hayırlı olanınızdır. Ben aileme en hayırlı olanınızım.", source: "Tirmizi", reference: "Tirmizi, Menakıb, 63; Hadis No: 3895", category: "aile", topic: "Aile" },
  { id: 195, text: "Dünya bir metadır, en hayırlı meta ise salih kadındır.", source: "Müslim", reference: "Müslim, Rada, 64; Hadis No: 1467", category: "aile", topic: "Evlilik" },
  { id: 196, text: "Bir kadın beş vakit namazını kılar, orucunu tutar, namusunu korur ve kocasına itaat ederse cennete girer.", source: "İbn Hibban", reference: "İbn Hibban, Sahih, 9/471; Hadis No: 4163", category: "aile", topic: "Kadın" },
  { id: 197, text: "Kadınlara iyi davranmanızı tavsiye ederim.", source: "Buhari, Müslim", reference: "Buhari, Nikah, 97; Müslim, Rada, 61", category: "aile", topic: "Kadın" },
  { id: 198, text: "Mümin erkek, mümin kadına kızmasın. Bir huyunu beğenmezse, başka huyunu beğenir.", source: "Müslim", reference: "Müslim, Rada, 61; Hadis No: 1469", category: "aile", topic: "Evlilik" },
  { id: 199, text: "Nikahı ilan edin.", source: "Tirmizi", reference: "Tirmizi, Nikah, 6; Hadis No: 1089", category: "aile", topic: "Nikah" },
  { id: 200, text: "Evlenmek benim sünnetimdir. Kim sünnetimden yüz çevirirse benden değildir.", source: "Buhari, Müslim", reference: "Buhari, Nikah, 1; Müslim, Nikah, 1", category: "aile", topic: "Evlilik" },
  { id: 201, text: "Nikahın en bereketlisi en kolay olanıdır.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 6/77", category: "aile", topic: "Nikah" },
  { id: 202, text: "Hala, anne yerindedir.", source: "Tirmizi", reference: "Tirmizi, Birr, 6; Hadis No: 1904", category: "aile", topic: "Hala" },
  { id: 203, text: "Dayı, baba yerindedir.", source: "Tirmizi", reference: "Tirmizi, Birr, 6; Hadis No: 1903", category: "aile", topic: "Dayı" },
  { id: 204, text: "Akrabayı ziyaret edin, ömrünüz uzasın ve rızkınız bollaşsın.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 1/443", category: "aile", topic: "Akraba Ziyareti" },
  { id: 205, text: "Sıla-i rahim ömrü uzatır.", source: "Buhari, Müslim", reference: "Buhari, Edeb, 12; Müslim, Birr, 20", category: "aile", topic: "Akraba Ziyareti" },
  { id: 206, text: "Aile nafakası en faziletli sadakadır.", source: "Müslim", reference: "Müslim, Zekat, 41; Hadis No: 995", category: "aile", topic: "Nafaka" },
  { id: 207, text: "Kişinin ailesine harcadığı her şey sadakadır.", source: "Buhari, Müslim", reference: "Buhari, Nafakat, 1; Müslim, Zekat, 46", category: "aile", topic: "Aile Harcaması" },
  { id: 208, text: "Eşinin ağzına koyduğun lokma bile sadakadır.", source: "Buhari, Müslim", reference: "Buhari, İman, 41; Müslim, Zekat, 49", category: "aile", topic: "Eş" },
  { id: 209, text: "Ev işlerinde eşine yardım edin.", source: "Buhari", reference: "Buhari, Edeb, 40; Hadis No: 6039", category: "aile", topic: "Ev İşleri" },
  { id: 210, text: "Kadının kocası üzerindeki hakkı: Yediğinden yedirmesi, giydiğinden giydirmesi.", source: "Ebu Davud", reference: "Ebu Davud, Nikah, 41; Hadis No: 2142", category: "aile", topic: "Kadın Hakkı" },
  { id: 211, text: "En iyi Müslüman, eşine en iyi davranan Müslümandır.", source: "Tirmizi", reference: "Tirmizi, Rada, 11; Hadis No: 1162", category: "aile", topic: "Eş" },
  { id: 212, text: "Üç kız veya üç kız kardeş büyütüp evlendiren cennete girer.", source: "Tirmizi", reference: "Tirmizi, Birr, 13; Hadis No: 1916", category: "aile", topic: "Kız Yetiştirmek" },
  { id: 213, text: "Çocuğunuza öpücük kondurmanız bile sadakadır.", source: "İbn Hibban", reference: "İbn Hibban, Sahih, 2/211", category: "aile", topic: "Çocuk Sevgisi" },
  { id: 214, text: "Çocuklarınızı sevin, onlara şefkat gösterin.", source: "İbn Mace", reference: "İbn Mace, Edeb, 3; Hadis No: 3665", category: "aile", topic: "Çocuk Sevgisi" },
  { id: 215, text: "Çocuklarınızı dövmeyin, onları güzellikle terbiye edin.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 4/382", category: "aile", topic: "Çocuk Terbiyesi" },
  { id: 216, text: "Kendi çocuğunuza yaptığınız harcama en faziletli sadakadır.", source: "Müslim", reference: "Müslim, Zekat, 42; Hadis No: 996", category: "aile", topic: "Çocuk" },
  { id: 217, text: "Kız çocuklarıyla imtihan edilen ve onlara güzel davranan için kızları cehenneme perde olur.", source: "Buhari, Müslim", reference: "Buhari, Zekat, 10; Müslim, Birr, 147", category: "aile", topic: "Kız Çocuğu" },
  { id: 218, text: "En hayırlı ev, içinde yetime iyilik edilen evdir.", source: "İbn Mace", reference: "İbn Mace, Edeb, 6; Hadis No: 3679", category: "aile", topic: "Yetim" },
  { id: 219, text: "Dul kadınlar ve yetimler için koşuşturan, Allah yolunda savaşan gibidir.", source: "Buhari", reference: "Buhari, Nafakat, 1; Hadis No: 5353", category: "aile", topic: "Dul ve Yetim" },
  { id: 220, text: "Evde oturan kadının sevabı, dışarıda çalışan erkeğin sevabı kadardır.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 24/212", category: "aile", topic: "Kadın" },

  // === İLİM HADİSLERİ (221-260) ===
  { id: 221, text: "İlim öğrenmek her Müslümana farzdır.", source: "İbn Mace", reference: "İbn Mace, Mukaddime, 17; Hadis No: 224", category: "ilim", topic: "İlim" },
  { id: 222, text: "İlim Çin'de bile olsa gidin öğrenin.", source: "İbn Abdilberr", reference: "İbn Abdilberr, Cami'u Beyani'l-İlm, 1/7", category: "ilim", topic: "İlim" },
  { id: 223, text: "Beşikten mezara kadar ilim öğrenin.", source: "İbn Abdilberr", reference: "İbn Abdilberr, Cami'u Beyani'l-İlm, 1/9", category: "ilim", topic: "İlim" },
  { id: 224, text: "Alimler peygamberlerin varisleridir.", source: "Tirmizi", reference: "Tirmizi, İlim, 19; Hadis No: 2682", category: "ilim", topic: "Alimler" },
  { id: 225, text: "Bir alimin ölümü, bir şehrin ölümünden büyüktür.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Evsat, 5/294", category: "ilim", topic: "Alimler" },
  { id: 226, text: "Kim ilim yoluna girerse, Allah ona cennet yolunu kolaylaştırır.", source: "Müslim", reference: "Müslim, Zikr, 38; Hadis No: 2699", category: "ilim", topic: "İlim" },
  { id: 227, text: "İlim öğrenen kimseye, karınca bile istiğfar eder.", source: "Tirmizi", reference: "Tirmizi, İlim, 19; Hadis No: 2682", category: "ilim", topic: "İlim" },
  { id: 228, text: "Hikmet müminin yitiğidir. Nerede bulursa alır.", source: "Tirmizi", reference: "Tirmizi, İlim, 19; Hadis No: 2687", category: "ilim", topic: "Hikmet" },
  { id: 229, text: "Sizin en hayırlınız Kur'an'ı öğrenen ve öğretendir.", source: "Buhari", reference: "Buhari, Fedailü'l-Kur'an, 21; Hadis No: 5027", category: "ilim", topic: "Kur'an Öğretimi" },
  { id: 230, text: "Faydasız ilimden Allah'a sığınırım.", source: "Müslim", reference: "Müslim, Zikr, 73; Hadis No: 2722", category: "ilim", topic: "Faydalı İlim" },
  { id: 231, text: "İlmi ile amel etmeyen alim, balını başkasına veren arı gibidir.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 10/150", category: "ilim", topic: "İlimle Amel" },
  { id: 232, text: "İlim iki türlüdür: Dilde olan ve kalpte olan. Kalpte olan faydalı ilimdir.", source: "Hakim", reference: "Hakim, el-Müstedrek, 1/109", category: "ilim", topic: "Faydalı İlim" },
  { id: 233, text: "Bir saat tefekkür, bir yıl ibadetten hayırlıdır.", source: "İbn Hibban", reference: "İbn Hibban, Ravdatü'l-Ukala, 1/47", category: "ilim", topic: "Tefekkür" },
  { id: 234, text: "İnsanın en büyük düşmanı cehalettir.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 38", category: "ilim", topic: "Cehalet" },
  { id: 235, text: "Bilmiyorsan sor. Cahilliğin ilacı sormaktır.", source: "Ebu Davud", reference: "Ebu Davud, Taharet, 125; Hadis No: 336", category: "ilim", topic: "Sormak" },
  { id: 236, text: "Öğretmek, bir yıl oruç tutmaktan hayırlıdır.", source: "İbn Mace", reference: "İbn Mace, Mukaddime, 20; Hadis No: 243", category: "ilim", topic: "Öğretmek" },
  { id: 237, text: "Her kim bir ilim öğretirse, onunla amel edenlerin sevabı kadar sevap alır.", source: "İbn Mace", reference: "İbn Mace, Mukaddime, 20; Hadis No: 240", category: "ilim", topic: "Öğretmek" },
  { id: 238, text: "İlmi yazarak bağlayın.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Evsat, 4/93", category: "ilim", topic: "Yazmak" },
  { id: 239, text: "Kim bir hayra yol gösterirse, o hayrı yapanın sevabı kadar sevap alır.", source: "Müslim", reference: "Müslim, İmare, 133; Hadis No: 1893", category: "ilim", topic: "Hayra Yol Göstermek" },
  { id: 240, text: "Okumak, düşünmek, anlamak her Müslümanın görevidir.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 75", category: "ilim", topic: "Okumak" },
  { id: 241, text: "En kıymetli miras ilimdir.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 54", category: "ilim", topic: "Miras" },
  { id: 242, text: "Akıl, Allah'ın insana verdiği en büyük nimettir.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 423", category: "ilim", topic: "Akıl" },
  { id: 243, text: "Düşünmeden konuşma, düşünmeden iş yapma.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 66", category: "ilim", topic: "Düşünmek" },
  { id: 244, text: "Bir mesele öğrenmek için utanma, çünkü utanan öğrenemez.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 82", category: "ilim", topic: "Öğrenmek" },
  { id: 245, text: "Cahil cesur, alim çekingen olur.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 107", category: "ilim", topic: "Cehalet" },
  { id: 246, text: "Kılıçlar körelir ama ilim körelmez.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 147", category: "ilim", topic: "İlim" },
  { id: 247, text: "İnsan konuşmadıkça gizli kalır.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 148", category: "ilim", topic: "Konuşma" },
  { id: 248, text: "Susmak hikmetin başıdır.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman, 4/256", category: "ilim", topic: "Susmak" },
  { id: 249, text: "Bir mecliste ilim konuşuluyorsa orası cennet bahçesidir.", source: "Tirmizi", reference: "Tirmizi, Deavat, 82; Hadis No: 3510", category: "ilim", topic: "İlim Meclisi" },
  { id: 250, text: "Tecrübe, ilmin en iyi öğretmenidir.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 211", category: "ilim", topic: "Tecrübe" },
  { id: 251, text: "Bilmediğini bil, bu da ilimdir.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 92", category: "ilim", topic: "İlim" },
  { id: 252, text: "Alim olmak istiyorsan önce öğrenci ol.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 125", category: "ilim", topic: "Öğrencilik" },
  { id: 253, text: "Çok soru sor ki çok öğren.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 95", category: "ilim", topic: "Sormak" },
  { id: 254, text: "Aklını kullanmayan, ayağını kullanır.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 142", category: "ilim", topic: "Akıl" },
  { id: 255, text: "Düşman olarak cehalet yeter, dost olarak akıl yeter.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 38", category: "ilim", topic: "Akıl-Cehalet" },
  { id: 256, text: "İlim, sahte dostları ayırt etmeni sağlar.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 265", category: "ilim", topic: "İlim" },
  { id: 257, text: "Az bilgi ile çok iş yapanı tebrik edin.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 4/131", category: "ilim", topic: "Uygulama" },
  { id: 258, text: "Allah'ım, faydalı ilim ver.", source: "İbn Mace", reference: "İbn Mace, Dua, 2; Hadis No: 3843", category: "ilim", topic: "Dua" },
  { id: 259, text: "Alimlere saygı gösterin, onlar peygamberlerin varisleridir.", source: "Taberani", reference: "Taberani, el-Mu'cemü's-Sağir, 2/80", category: "ilim", topic: "Alimlere Saygı" },
  { id: 260, text: "İlim ruhun gıdasıdır.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 98", category: "ilim", topic: "İlim" },

  // === DUA HADİSLERİ (261-300) ===
  { id: 261, text: "Dua ibadetin özüdür.", source: "Tirmizi", reference: "Tirmizi, Deavat, 1; Hadis No: 3371", category: "dua", topic: "Dua" },
  { id: 262, text: "Dua müminin silahıdır.", source: "Hakim", reference: "Hakim, el-Müstedrek, 1/492", category: "dua", topic: "Dua" },
  { id: 263, text: "Dua etmekten aciz olmayın, dua eden helak olmaz.", source: "Hakim", reference: "Hakim, el-Müstedrek, 1/493", category: "dua", topic: "Dua" },
  { id: 264, text: "Duanın en faziletlisi, kardeşinin gıyabında ettiğin duadır.", source: "Ebu Davud", reference: "Ebu Davud, Salat, 364; Hadis No: 1534", category: "dua", topic: "Kardeşe Dua" },
  { id: 265, text: "Bir Müslüman, kardeşi için gıyabında dua ederse, melek 'Sana da aynısı olsun' der.", source: "Müslim", reference: "Müslim, Zikr, 87; Hadis No: 2733", category: "dua", topic: "Gıyabi Dua" },
  { id: 266, text: "Üç duanın kabul olacağından şüphe yoktur: Mazlumun duası, yolcunun duası, anne-babanın çocuğuna duası.", source: "Tirmizi", reference: "Tirmizi, Birr, 7; Hadis No: 1905", category: "dua", topic: "Makbul Dua" },
  { id: 267, text: "Duayı kabul olunca kes. Dua edin, kabul olunacak. Sabredin, sabır güzeldir.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 2/442", category: "dua", topic: "Sabır" },
  { id: 268, text: "Allah'tan istemeyen kimseye Allah gazap eder.", source: "Tirmizi", reference: "Tirmizi, Deavat, 2; Hadis No: 3373", category: "dua", topic: "İstemek" },
  { id: 269, text: "Rabbiniz hayâ sahibi ve kerimdir. Kul elini kaldırınca boş çevirmekten hayâ eder.", source: "Ebu Davud", reference: "Ebu Davud, Salat, 358; Hadis No: 1488", category: "dua", topic: "Elleri Kaldırmak" },
  { id: 270, text: "Ezan ile kamet arasında yapılan dua reddedilmez.", source: "Tirmizi", reference: "Tirmizi, Salat, 44; Hadis No: 212", category: "dua", topic: "Ezan Vakti" },
  { id: 271, text: "Secde halinde çokça dua edin.", source: "Müslim", reference: "Müslim, Salat, 215; Hadis No: 482", category: "dua", topic: "Secde Duası" },
  { id: 272, text: "Cuma günü öyle bir saat vardır ki, o saatte yapılan dua kabul olunur.", source: "Buhari, Müslim", reference: "Buhari, Cuma, 37; Müslim, Cuma, 13", category: "dua", topic: "Cuma Saati" },
  { id: 273, text: "Gecenin son üçte birinde dua kabul olunur.", source: "Buhari, Müslim", reference: "Buhari, Teheccüd, 14; Müslim, Müsafirin, 166", category: "dua", topic: "Gece Duası" },
  { id: 274, text: "Allah her gece dünya semasına iner ve 'Bana dua eden yok mu, dua etsin kabul edeyim' der.", source: "Buhari, Müslim", reference: "Buhari, Teheccüd, 14; Müslim, Müsafirin, 168", category: "dua", topic: "Gece" },
  { id: 275, text: "Her kim sabah akşam üç kez 'Bismillahillezi la yedurru maa ismihi şey'un' derse ona hiçbir şey zarar vermez.", source: "Tirmizi", reference: "Tirmizi, Deavat, 13; Hadis No: 3388", category: "dua", topic: "Sabah-Akşam Zikri" },
  { id: 276, text: "Subhanallahi ve bihamdihi günde yüz kez söyleyen, günahları deniz köpüğü kadar olsa bağışlanır.", source: "Buhari, Müslim", reference: "Buhari, Deavat, 65; Müslim, Zikr, 28", category: "dua", topic: "Tesbih" },
  { id: 277, text: "La ilahe illallahu vahdehu la şerike leh, lehul mülkü ve lehul hamdu ve hüve ala külli şey'in kadir' günde yüz kez söyleyene on köle azat etmiş gibi sevap yazılır.", source: "Buhari, Müslim", reference: "Buhari, Deavat, 64; Müslim, Zikr, 28", category: "dua", topic: "Kelime-i Tevhid" },
  { id: 278, text: "İstiğfar eden günahlarından temizlenir.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 5/378", category: "dua", topic: "İstiğfar" },
  { id: 279, text: "Seyyidü'l-istiğfar: Allah'ım, sen benim Rabbimsin, senden başka ilah yoktur. Beni sen yarattın...", source: "Buhari", reference: "Buhari, Deavat, 2; Hadis No: 6306", category: "dua", topic: "İstiğfar" },
  { id: 280, text: "Her kim yüz kez istiğfar ederse, Allah onu her sıkıntıdan kurtarır.", source: "Ebu Davud", reference: "Ebu Davud, Salat, 361; Hadis No: 1518", category: "dua", topic: "İstiğfar" },
  { id: 281, text: "Her kim 'La havle ve la kuvvete illa billah' derse, doksan dokuz derde deva olur.", source: "Hakim", reference: "Hakim, el-Müstedrek, 1/528", category: "dua", topic: "Havkale" },
  { id: 282, text: "Salavat getirin, çünkü bana getirilen salavat size on misli döner.", source: "Müslim", reference: "Müslim, Salat, 70; Hadis No: 408", category: "dua", topic: "Salavat" },
  { id: 283, text: "Kim bana bir kez salavat getirirse, Allah ona on kez salat eder.", source: "Müslim", reference: "Müslim, Salat, 70; Hadis No: 408", category: "dua", topic: "Salavat" },
  { id: 284, text: "Cuma günü bana çokça salavat getirin.", source: "Ebu Davud", reference: "Ebu Davud, Salat, 201; Hadis No: 1047", category: "dua", topic: "Cuma Salavatı" },
  { id: 285, text: "Hamd ile başlayan ve salavat ile biten dua kabul olunur.", source: "Tirmizi", reference: "Tirmizi, Deavat, 64; Hadis No: 3476", category: "dua", topic: "Dua Adabı" },
  { id: 286, text: "Yemekten sonra 'Elhamdulillahillezi et'ameni ve razakani' diyen günahları bağışlanır.", source: "Tirmizi", reference: "Tirmizi, Deavat, 55; Hadis No: 3458", category: "dua", topic: "Yemek Duası" },
  { id: 287, text: "Evden çıkarken 'Bismillahi tevekkeltü alallah' diyen korunur.", source: "Tirmizi", reference: "Tirmizi, Deavat, 34; Hadis No: 3426", category: "dua", topic: "Evden Çıkış Duası" },
  { id: 288, text: "Tuvalete girerken sol ayakla gir ve dua oku.", source: "Buhari", reference: "Buhari, Vudu, 9; Hadis No: 142", category: "dua", topic: "Tuvalet Duası" },
  { id: 289, text: "Uyumadan önce Ayetü'l-Kürsi okuyan, sabaha kadar korunur.", source: "Buhari", reference: "Buhari, Vekalet, 10; Hadis No: 2311", category: "dua", topic: "Uyku Duası" },
  { id: 290, text: "Uykudan uyanınca 'Elhamdulillahillezi ahyana' de.", source: "Buhari", reference: "Buhari, Deavat, 8; Hadis No: 6312", category: "dua", topic: "Uyanma Duası" },
  { id: 291, text: "Yolculuğa çıkarken dua et, yolculuk duası makbuldür.", source: "Tirmizi", reference: "Tirmizi, Deavat, 45; Hadis No: 3438", category: "dua", topic: "Yolculuk Duası" },
  { id: 292, text: "Sıkıntı anında 'La ilahe illallahul azimul halim' de.", source: "Buhari, Müslim", reference: "Buhari, Deavat, 28; Müslim, Zikr, 83", category: "dua", topic: "Sıkıntı Duası" },
  { id: 293, text: "Her işe başlarken 'Bismillah' de.", source: "Ebu Davud", reference: "Ebu Davud, Et'ime, 15; Hadis No: 3767", category: "dua", topic: "Besmele" },
  { id: 294, text: "Besmele ile başlanmayan her iş bereketsizdir.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 2/359", category: "dua", topic: "Besmele" },
  { id: 295, text: "Rabbena atina fiddunya haseneten ve fil ahireti haseneten ve kına azabennar.", source: "Buhari, Müslim", reference: "Buhari, Deavat, 55; Müslim, Zikr, 26", category: "dua", topic: "Dünya-Ahiret Duası" },
  { id: 296, text: "Allah'ım, affını, sağlık ve afiyetini isterim.", source: "Tirmizi", reference: "Tirmizi, Deavat, 85; Hadis No: 3514", category: "dua", topic: "Afiyet Duası" },
  { id: 297, text: "Çokça 'Subhanallahi ve bihamdihi, subhanallahil azim' de.", source: "Buhari", reference: "Buhari, Deavat, 65; Hadis No: 6406", category: "dua", topic: "Tesbih" },
  { id: 298, text: "En faziletli zikir 'La ilahe illallah'tır.", source: "Tirmizi", reference: "Tirmizi, Deavat, 9; Hadis No: 3383", category: "dua", topic: "Zikir" },
  { id: 299, text: "Dile hafif, mizana ağır iki kelime: Subhanallahi ve bihamdihi, subhanallahil azim.", source: "Buhari, Müslim", reference: "Buhari, Deavat, 65; Müslim, Zikr, 31", category: "dua", topic: "Tesbih" },
  { id: 300, text: "Kim bir yere varınca 'Euzu bi kelimatillahit tammati' derse oradan ayrılana kadar bir şey ona zarar vermez.", source: "Müslim", reference: "Müslim, Zikr, 54; Hadis No: 2708", category: "dua", topic: "Sığınma Duası" },

  // === AHİRET HADİSLERİ (301-340) ===
  { id: 301, text: "Ölümü çok anın, çünkü dünyaya olan bağlılığı keser.", source: "Tirmizi", reference: "Tirmizi, Zühd, 4; Hadis No: 2307", category: "ahiret", topic: "Ölümü Anmak" },
  { id: 302, text: "Ölüm, müminin hediyesidir.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Evsat, 3/358", category: "ahiret", topic: "Ölüm" },
  { id: 303, text: "Bu dünya ahiretin tarlasıdır.", source: "Deylemi", reference: "Deylemi, Firdevsü'l-Ahbar, 3/62", category: "ahiret", topic: "Dünya" },
  { id: 304, text: "Akıllı kişi nefsini hesaba çeken ve ölüm sonrası için çalışandır.", source: "Tirmizi", reference: "Tirmizi, Kıyame, 25; Hadis No: 2459", category: "ahiret", topic: "Hesap" },
  { id: 305, text: "Hesaba çekilmeden önce kendinizi hesaba çekin.", source: "Tirmizi", reference: "Tirmizi, Kıyame, 25; Hadis No: 2459", category: "ahiret", topic: "Nefis Muhasebesi" },
  { id: 306, text: "İki nimet vardır ki insanların çoğu bunlarda aldanmıştır: Sağlık ve boş vakit.", source: "Buhari", reference: "Buhari, Rikak, 1; Hadis No: 6412", category: "ahiret", topic: "Vakit" },
  { id: 307, text: "Kıyamet günü hiçbir kul, ömrünü nerede tükettiği sorulmadan bir adım atamaz.", source: "Tirmizi", reference: "Tirmizi, Kıyame, 1; Hadis No: 2417", category: "ahiret", topic: "Hesap" },
  { id: 308, text: "Dünyada garip veya yolcu gibi ol.", source: "Buhari", reference: "Buhari, Rikak, 3; Hadis No: 6416", category: "ahiret", topic: "Dünya" },
  { id: 309, text: "Dünya sevgisi bütün hataların başıdır.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman, 7/338", category: "ahiret", topic: "Dünya Sevgisi" },
  { id: 310, text: "Dünya müminin zindanı, kafirin cennetidir.", source: "Müslim", reference: "Müslim, Zühd, 1; Hadis No: 2956", category: "ahiret", topic: "Dünya" },
  { id: 311, text: "Allah katında dünyanın değeri bir sivrisinek kanadı kadar bile değildir.", source: "Tirmizi", reference: "Tirmizi, Zühd, 13; Hadis No: 2320", category: "ahiret", topic: "Dünyanın Değeri" },
  { id: 312, text: "Cennet, hoşlanmadığınız şeylerle çevrilidir.", source: "Buhari, Müslim", reference: "Buhari, Rikak, 28; Müslim, Cennet, 1", category: "ahiret", topic: "Cennet" },
  { id: 313, text: "Cehennem, şehvetlerle çevrilidir.", source: "Buhari, Müslim", reference: "Buhari, Rikak, 28; Müslim, Cennet, 1", category: "ahiret", topic: "Cehennem" },
  { id: 314, text: "Cennete ilk girecek olan fakir muhacirlerdir.", source: "Tirmizi", reference: "Tirmizi, Zühd, 37; Hadis No: 2352", category: "ahiret", topic: "Cennet" },
  { id: 315, text: "Cennette öyle bir ağaç var ki, gölgesinde yüz yıl yürüsen bitiremezsin.", source: "Buhari, Müslim", reference: "Buhari, Bed'ü'l-Halk, 8; Müslim, Cennet, 6", category: "ahiret", topic: "Cennet" },
  { id: 316, text: "Cennette kimsenin görmediği, duymadığı ve aklına gelmediği nimetler vardır.", source: "Buhari, Müslim", reference: "Buhari, Bed'ü'l-Halk, 8; Müslim, Cennet, 2", category: "ahiret", topic: "Cennet" },
  { id: 317, text: "Cennete ancak mümin girer.", source: "Müslim", reference: "Müslim, İman, 93; Hadis No: 54", category: "ahiret", topic: "Cennet" },
  { id: 318, text: "Bir dirhem riba, otuz üç zina günahından ağırdır.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 5/225", category: "ahiret", topic: "Faiz" },
  { id: 319, text: "Haramla beslenen vücut cennete giremez.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 3/399", category: "ahiret", topic: "Haram" },
  { id: 320, text: "Kabir, ya cennet bahçelerinden bir bahçe ya da cehennem çukurlarından bir çukurdur.", source: "Tirmizi", reference: "Tirmizi, Kıyame, 26; Hadis No: 2460", category: "ahiret", topic: "Kabir" },
  { id: 321, text: "Kabir azabından Allah'a sığının.", source: "Buhari, Müslim", reference: "Buhari, Cenaiz, 87; Müslim, Mesacid, 130", category: "ahiret", topic: "Kabir Azabı" },
  { id: 322, text: "İnsan ölünce üç şey hariç ameli kesilir: Sadaka-i cariye, faydalı ilim, dua eden hayırlı evlat.", source: "Müslim", reference: "Müslim, Vasiyyet, 14; Hadis No: 1631", category: "ahiret", topic: "Devam Eden Amel" },
  { id: 323, text: "Kıyamet günü insanların en çok pişman olacağı şey, dünyada boş geçirdiği vakitlerdir.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 8/158", category: "ahiret", topic: "Pişmanlık" },
  { id: 324, text: "Kıyamet günü ilk sorulacak şey namazdır.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Evsat, 2/53", category: "ahiret", topic: "Namaz" },
  { id: 325, text: "Sırat köprüsü kıldan ince, kılıçtan keskindir.", source: "Müslim", reference: "Müslim, İman, 302; Hadis No: 183", category: "ahiret", topic: "Sırat" },
  { id: 326, text: "Şefaatime en layık olan, bana en çok salavat getirendir.", source: "Tirmizi", reference: "Tirmizi, Vitr, 21; Hadis No: 484", category: "ahiret", topic: "Şefaat" },
  { id: 327, text: "Kim ihlaslı olarak 'La ilahe illallah' derse cennete girer.", source: "Buhari, Müslim", reference: "Buhari, İlim, 33; Müslim, İman, 47", category: "ahiret", topic: "Cennet" },
  { id: 328, text: "Cennetin kapıları sekizdir, bir tanesi Reyyan'dır, oradan sadece oruçlular girer.", source: "Buhari, Müslim", reference: "Buhari, Savm, 4; Müslim, Sıyam, 166", category: "ahiret", topic: "Cennet Kapıları" },
  { id: 329, text: "Ümmetim kıyamet günü, abdest izlerinden parlak gelecektir.", source: "Müslim", reference: "Müslim, Taharet, 35; Hadis No: 246", category: "ahiret", topic: "Abdest" },
  { id: 330, text: "Kur'an okuyun, o kıyamet günü şefaatçi olacaktır.", source: "Müslim", reference: "Müslim, Müsafirin, 252; Hadis No: 804", category: "ahiret", topic: "Kur'an Şefaati" },
  { id: 331, text: "Kim 'La ilahe illallah' diyerek ölürse cennete girer.", source: "Hakim", reference: "Hakim, el-Müstedrek, 1/351", category: "ahiret", topic: "Son Söz" },
  { id: 332, text: "Her nefis ölümü tadacaktır.", source: "Kur'an (Al-i İmran 185)", reference: "Kur'an-ı Kerim, Al-i İmran Suresi, 3/185", category: "ahiret", topic: "Ölüm" },
  { id: 333, text: "Kim Allah'a kavuşmayı isterse, Allah da ona kavuşmayı ister.", source: "Buhari", reference: "Buhari, Rikak, 41; Hadis No: 6507", category: "ahiret", topic: "Allah'a Kavuşma" },
  { id: 334, text: "Şüphesiz Allah kulunun tövbesini, canı boğazına gelmeden kabul eder.", source: "Tirmizi", reference: "Tirmizi, Deavat, 98; Hadis No: 3537", category: "ahiret", topic: "Tövbe" },
  { id: 335, text: "Mümin, ölüm döşeğinde dağdan daha sabırlı olmalıdır.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 4/287", category: "ahiret", topic: "Sabır" },
  { id: 336, text: "Ölümü sevenle Allah'a kavuşmak, dünya hayatından daha tatlıdır.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 193", category: "ahiret", topic: "Ölüm" },
  { id: 337, text: "Günahların en büyüğü, sahibinin önemsemediği günahtır.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman, 5/447", category: "ahiret", topic: "Günah" },
  { id: 338, text: "Her günahın bir cezası vardır, Allah dilerse affeder, dilerse cezalandırır.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Kebir, 11/236", category: "ahiret", topic: "Ceza" },
  { id: 339, text: "Allah'ın rahmetinden ümit kesmeyin.", source: "Kur'an (Zümer 53)", reference: "Kur'an-ı Kerim, Zümer Suresi, 39/53", category: "ahiret", topic: "Ümit" },
  { id: 340, text: "Allah'ın rahmeti gazabını geçmiştir.", source: "Buhari, Müslim", reference: "Buhari, Tevhid, 15; Müslim, Tevbe, 14", category: "ahiret", topic: "Rahmet" },

  // === SABIR VE ŞÜKÜR HADİSLERİ (341-365) ===
  { id: 341, text: "Sabır, musibetin ilk anında gösterilendir.", source: "Buhari, Müslim", reference: "Buhari, Cenaiz, 32; Müslim, Cenaiz, 14", category: "sabir", topic: "Sabır" },
  { id: 342, text: "Sabredenlerle beraber olun.", source: "Kur'an (Enfal 46)", reference: "Kur'an-ı Kerim, Enfal Suresi, 8/46", category: "sabir", topic: "Sabır" },
  { id: 343, text: "Sabır ve namaz ile Allah'tan yardım isteyin.", source: "Kur'an (Bakara 45)", reference: "Kur'an-ı Kerim, Bakara Suresi, 2/45", category: "sabir", topic: "Sabır ve Namaz" },
  { id: 344, text: "Müminin işi ne güzeldir! Her işi hayırdır. Bu sadece mümine mahsustur.", source: "Müslim", reference: "Müslim, Zühd, 64; Hadis No: 2999", category: "sabir", topic: "Mümin" },
  { id: 345, text: "Başa gelen musibetlere sabretmek büyük bir fazilettir.", source: "Tirmizi", reference: "Tirmizi, Zühd, 57; Hadis No: 2396", category: "sabir", topic: "Musibet" },
  { id: 346, text: "Allah bir kulunu severse onu imtihan eder.", source: "Tirmizi", reference: "Tirmizi, Zühd, 57; Hadis No: 2396", category: "sabir", topic: "İmtihan" },
  { id: 347, text: "Musibet büyüdükçe mükafat büyür.", source: "Tirmizi", reference: "Tirmizi, Zühd, 57; Hadis No: 2396", category: "sabir", topic: "Mükafat" },
  { id: 348, text: "Her sıkıntıdan sonra bir ferahlık vardır.", source: "Kur'an (İnşirah 5-6)", reference: "Kur'an-ı Kerim, İnşirah Suresi, 94/5-6", category: "sabir", topic: "Ferahlık" },
  { id: 349, text: "Allah sabredenleri sever.", source: "Kur'an (Al-i İmran 146)", reference: "Kur'an-ı Kerim, Al-i İmran Suresi, 3/146", category: "sabir", topic: "Sabır" },
  { id: 350, text: "Sabredenler, mükafatlarını hesapsız alacaklardır.", source: "Kur'an (Zümer 10)", reference: "Kur'an-ı Kerim, Zümer Suresi, 39/10", category: "sabir", topic: "Mükafat" },
  { id: 351, text: "Şükretmek nimeti artırır.", source: "Kur'an (İbrahim 7)", reference: "Kur'an-ı Kerim, İbrahim Suresi, 14/7", category: "sabir", topic: "Şükür" },
  { id: 352, text: "Şükredenleri Allah sever.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 4/146", category: "sabir", topic: "Şükür" },
  { id: 353, text: "Az olana şükreden, çok bulur.", source: "Taberani", reference: "Taberani, el-Mu'cemü'l-Evsat, 4/237", category: "sabir", topic: "Şükür" },
  { id: 354, text: "Nimete şükret, sıkıntıya sabret.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman, 4/97", category: "sabir", topic: "Şükür ve Sabır" },
  { id: 355, text: "Her şeyde bir hayır vardır.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 4/332", category: "sabir", topic: "Hayır" },
  { id: 356, text: "Kalbinizde Allah'a güven olsun, O en iyi yardımcıdır.", source: "Tirmizi", reference: "Tirmizi, Deavat, 79; Hadis No: 3504", category: "sabir", topic: "Tevekkül" },
  { id: 357, text: "Allah'a tevekkül et, O sana yeter.", source: "Kur'an (Talak 3)", reference: "Kur'an-ı Kerim, Talak Suresi, 65/3", category: "sabir", topic: "Tevekkül" },
  { id: 358, text: "Allah'a güvenen aldanmaz.", source: "Ahmed", reference: "Ahmed b. Hanbel, Müsned, 5/183", category: "sabir", topic: "Güven" },
  { id: 359, text: "Allah'tan korkana bir çıkış yolu verilir.", source: "Kur'an (Talak 2)", reference: "Kur'an-ı Kerim, Talak Suresi, 65/2", category: "sabir", topic: "Takva" },
  { id: 360, text: "Allah kuluna kafi olandır.", source: "Kur'an (Zümer 36)", reference: "Kur'an-ı Kerim, Zümer Suresi, 39/36", category: "sabir", topic: "Kafi" },
  { id: 361, text: "Zorlukla beraber kolaylık vardır.", source: "Kur'an (İnşirah 6)", reference: "Kur'an-ı Kerim, İnşirah Suresi, 94/6", category: "sabir", topic: "Kolaylık" },
  { id: 362, text: "Rabbiniz çok bağışlayıcı ve çok merhametlidir.", source: "Kur'an", reference: "Kur'an-ı Kerim, En'am Suresi, 6/54", category: "sabir", topic: "Merhamet" },
  { id: 363, text: "Kim Allah'a güvenirse O ona yeter.", source: "Kur'an (Talak 3)", reference: "Kur'an-ı Kerim, Talak Suresi, 65/3", category: "sabir", topic: "Tevekkül" },
  { id: 364, text: "Musibete sabretmenin karşılığı cennettir.", source: "Tirmizi", reference: "Tirmizi, Zühd, 57; Hadis No: 2399", category: "sabir", topic: "Cennet" },
  { id: 365, text: "Her güne şükreden, her güneşin doğuşunu kutlar.", source: "İmam Ali", reference: "Nehcü'l-Belağa, Hikmetler, 289", category: "sabir", topic: "Şükür" },
  { id: 366, text: "İman iki yarımdır: Yarısı sabır, yarısı şükürdür.", source: "Beyhaki", reference: "Beyhaki, Şuabü'l-İman, 4/102", category: "sabir", topic: "İman" },
];

// Günün hadisini getir (yılın gününe göre)
export function getDailyHadithFromCollection(): HadithData {
  const now = new Date();
  const start = new Date(now.getFullYear(), 0, 0);
  const diff = now.getTime() - start.getTime();
  const dayOfYear = Math.floor(diff / (1000 * 60 * 60 * 24));
  const index = dayOfYear % HADITHS.length;
  return HADITHS[index];
}

// Kategoriye göre hadisleri filtrele
export function getHadithsByCategory(category: HadithCategory): HadithData[] {
  return HADITHS.filter(h => h.category === category);
}

// Rastgele hadis getir
export function getRandomHadithFromCollection(): HadithData {
  const randomIndex = Math.floor(Math.random() * HADITHS.length);
  return HADITHS[randomIndex];
}

// Hadis ara
export function searchHadithsInCollection(query: string): HadithData[] {
  const lowerQuery = query.toLowerCase();
  return HADITHS.filter(h =>
    h.text.toLowerCase().includes(lowerQuery) ||
    h.topic.toLowerCase().includes(lowerQuery) ||
    h.source.toLowerCase().includes(lowerQuery)
  );
}
